# -*- coding: utf-8 -*-
from typing import Optional

from pydantic import BaseModel, Field


class BailianError(Exception):
    """
    Base error class for the SDK.
    """

    _name: str = "InternalServerError"
    _code: int = 500
    _message: str = "InternalServerError from Bailian service."
    _type: str = "InternalServerError"
    _request_id: str = ""

    @property
    def name(self) -> str:
        """Get the error name.

        Returns:
            str: The error name from the class hierarchy.
        """
        # Gather _name from all classes in MRO that are subclasses of BaseError
        return self._name

    def set_request_id(self, request_id: str) -> None:
        """Set the request ID for this error.

        Args:
            request_id (str): The request ID to associate with this error.
        """
        self._request_id = request_id

    @property
    def request_id(self) -> str:
        """Get the request ID associated with this error.

        Returns:
            str: The request ID, or empty string if not set.
        """
        return self._request_id

    @property
    def code(self) -> int:
        """Get the HTTP status code for this error.

        Returns:
            int: The first 3 digits of the error code as an integer.
        """
        return int(str(self._code)[0:3])

    @property
    def type(self) -> str:
        """Get the error type.

        Returns:
            str: The error type classification.
        """
        return self._type

    @property
    def message(self) -> str:
        """Get the formatted error message.

        Returns:
            str: The error message, optionally including the request ID.
        """
        result = self._message
        if self._request_id != "":
            result = f"{result} with request id {self._request_id}"
        return result

    def __init__(self, *args: str) -> None:
        """Initialize the BailianError.

        Args:
            *args (str): Variable arguments for error message formatting.
                If the message contains '{}' placeholders, args will be used
                to format the message. Otherwise, the first arg replaces
                the entire message.
        """
        substring = "{}"
        # for those with pre-defined format whose message will not be replaced
        if substring in self._message:
            self._message = self._message.format(*args)
        # for those whose message will be replaced by the input message
        else:
            if len(args) > 0 and isinstance(args[0], str):
                self._message = args[0]


class ErrorModel(BaseModel):
    """Pydantic model for representing error information."""

    name: str = Field(..., description="Error message")
    code: Optional[int] = Field(None, description="Error code")
    message: str = Field(..., description="Error message")

    @classmethod
    def from_exception(cls, exc: BailianError) -> "ErrorModel":
        """Create an ErrorModel from a BailianError exception.

        Args:
            exc (BailianError): The exception to convert.

        Returns:
            ErrorModel: A new ErrorModel instance with data from the exception.
        """
        return cls(
            message=exc.message,
            code=exc.code,
            name=exc.name,
        )
